import React from 'react';
import axios from 'axios';

export default class WuphfForm extends React.Component {
  constructor() {
    super();

    this.state = {
      formFields: { from_name: '' }
    };
  }

  handleSubmit(formFields) {
    axios
      .post('/wuphf', formFields)
      .then(function(response) {
        console.log(response);
        //Perform action based on response
      })
      .catch(function(error) {
        console.log(error);
        //Perform action based on error
      });
  }

  inputChangeHandler(e) {
    let formFields = { ...this.state.formFields };
    formFields[e.target.name] = e.target.value;
    this.setState({
      formFields
    });
  }

  render() {
    return (
      <div className='wuphf-form'>
        <form onsubmit={this.handleSubmit(this.state.formFields)}>
          <label for='from_name'>Your Name</label>
          <input
            type='text'
            id='from_name'
            name='from_name'
            placeholder='Your name..'
            onChange={e => this.inputChangeHandler.call(this, e)}
            value={this.state.formFields.username}
          />

          <label for='lname'>Last Name</label>
          <input
            type='text'
            id='lname'
            name='lastname'
            placeholder='Your last name..'
          />

          <button class='btn btn-primary'>Send Wuphf!</button>
        </form>
      </div>
    );
  }
}
