import React from 'react';
// import axios from 'axios';
import './WuphfForm.css';

export default class WuphfForm extends React.Component {
  constructor() {
    super();
    this.state = {
      formFields: {
        from_name: '',
        sms_number: '',
        call_number: '',
        to_email: '',
        message: ''
      }
    };
  }

  handleSubmit(event) {
    event.preventDefault();
    const data = new FormData(event.target);
    console.log(data);
    // axios
    //   .post('/wuphf', this.state.formFields)
    //   .then(function(response) {
    //     console.log(response);
    //     //Perform action based on response
    //   })
    //   .catch(function(error) {
    //     console.log(error);
    //     //Perform action based on error
    //   });
  }

  render() {
    return (
      <div className='wuphf-form'>
        <form onSubmit={this.handleSubmit}>
          <label className='wuphf-form-label' htmlFor='from_name'>
            Your Name
          </label>
          <input
            type='text'
            id='from_name'
            name='from_name'
            placeholder="So they know who it's from..."
          />
          <label className='wuphf-form-label' htmlFor='sms_number'>
            SMS Number
          </label>
          <input
            type='text'
            id='sms_number'
            name='sms_number'
            placeholder='Phone number to text...'
          />
          <button className='send-button'>Send Wuphf!</button>
        </form>
      </div>
    );
  }
}
