import React from 'react';
import axios from 'axios';
import './WuphfForm.css';

const formOptions = [
  {
    id: 'from_name',
    labelText: 'Your Name',
    placeholder: "So they know who it's from..."
  }
];

export default class WuphfForm extends React.Component {
  constructor() {
    super();
    this.state = {
      formFields: {
        from_name: '',
        sms_number: '',
        call_number: '',
        to_email: '',
        message: ''
      }
    };
  }

  onFormSubmit(e) {
    e.preventDefault();
    console.log(this.state.formFields);
    axios
      .post('/api/wuphf', this.state.formFields)
      .then(function(response) {
        console.log(response);
        //Perform action based on response
      })
      .catch(function(error) {
        console.log(error);
        //Perform action based on error
      });
  }

  handleChange(e) {
    let formFields = { ...this.state.formFields };
    formFields[e.target.name] = e.target.value;
    console.log(formFields);
    this.setState({
      formFields
    });
  }

  render() {
    return (
      <div className='wuphf-form'>
        <form onSubmit={this.onFormSubmit.bind(this)}>
          <label className='wuphf-form-label' htmlFor='from_name'>
            Your Name
          </label>
          <input
            type='text'
            id='from_name'
            name='from_name'
            placeholder="So they know who it's from..."
            onChange={this.handleChange.bind(this)}
            value={this.state.formFields.username}
          />
          <label className='wuphf-form-label' htmlFor='sms_number'>
            SMS Number
          </label>
          <input
            type='text'
            id='sms_number'
            name='sms_number'
            placeholder='Phone number to text...'
            onChange={this.handleChange.bind(this)}
            value={this.state.formFields.sms_number}
          />
          <button className='send-button'>Send Wuphf!</button>
        </form>
      </div>
    );
  }
}
