import React from 'react';
// import axios from 'axios';
import './WuphfForm.css';

export default class WuphfForm extends React.Component {
  constructor() {
    super();
    this.state = {
      formFields: {
        from_name: '',
        sms_number: '',
        call_number: '',
        to_email: '',
        message: ''
      }
    };
  }

  handleSubmit() {
    console.log(this.state);
    // axios
    //   .post('/wuphf', this.state.formFields)
    //   .then(function(response) {
    //     console.log(response);
    //     //Perform action based on response
    //   })
    //   .catch(function(error) {
    //     console.log(error);
    //     //Perform action based on error
    //   });
  }

  onFormSubmit(e) {
    e.preventDefault();
    this.props.onFormSubmit(this.state.form);
  }

  handleChange(event) {
    let form = this.state.form;
    form.email = event.target.value;
    this.setState({
      form: form
    });
  }

  inputChangeHandler(e) {
    let formFields = { ...this.state.formFields };
    formFields[e.target.name] = e.target.value;
    this.setState({
      formFields
    });
  }

  render() {
    return (
      <div className='wuphf-form'>
        <form onSubmit={this.handleSubmit.bind(this)}>
          <label className='wuphf-form-label' htmlFor='from_name'>
            Your Name
          </label>
          <input
            type='text'
            id='from_name'
            name='from_name'
            placeholder="So they know who it's from..."
            onChange={e => this.inputChangeHandler(e)}
            value={this.state.formFields.username}
          />
          <label className='wuphf-form-label' htmlFor='sms_number'>
            SMS Number
          </label>
          <input
            type='text'
            id='sms_number'
            name='sms_number'
            placeholder='Phone number to text...'
            onChange={e => this.inputChangeHandler.call(this, e)}
            value={this.state.formFields.sms_number}
          />
          <button className='send-button'>Send Wuphf!</button>
        </form>
      </div>
    );
  }
}
