import React from 'react';
import axios from 'axios';
import './WuphfForm.css';

export default class WuphfForm extends React.Component {
  constructor() {
    super();

    this.state = {
      formFields: {
        from_name: '',
        sms_number: '',
        call_number: '',
        to_email: '',
        message: ''
      }
    };
  }

  handleSubmit(formFields) {
    axios
      .post('/wuphf', formFields)
      .then(function(response) {
        console.log(response);
        //Perform action based on response
      })
      .catch(function(error) {
        console.log(error);
        //Perform action based on error
      });
  }

  inputChangeHandler(e) {
    let formFields = { ...this.state.formFields };
    formFields[e.target.name] = e.target.value;
    this.setState({
      formFields
    });
  }

  render() {
    return (
      <div className='wuphf-form'>
        <form onsubmit={this.handleSubmit(this.state.formFields)}>
          <label className='wuphf-form-label' for='from_name'>
            Your Name
          </label>
          <input
            type='text'
            id='from_name'
            name='from_name'
            placeholder="So they know who it's from..."
            onChange={e => this.inputChangeHandler.call(this, e)}
            value={this.state.formFields.username}
          />
          <label className='wuphf-form-label' for='sms_number'>
            SMS Number
          </label>
          <input
            type='text'
            id='sms_number'
            name='sms_number'
            placeholder='Phone number to text'
            onChange={e => this.inputChangeHandler.call(this, e)}
            value={this.state.formFields.username}
          />
          <button class='send-button'>Send Wuphf!</button>
        </form>
      </div>
    );
  }
}
