import React from 'react';
import axios from 'axios';
import './WuphfForm.css';

const styles = {
  input_type__text: {
    width: '100%',
    padding: '12px 20px',
    margin: '8px 0',
    display: 'inline-block',
    border: '1px solid #ccc',
    borderRadius: '4px',
    boxSizing: 'border-box'
  },
  wuphf_form: {
    borderRadius: '5px',
    backgroundColor: '#f2f2f2',
    padding: '20px',
    width: '40%',
    margin: 'auto'
  },
  label: { fontWeight: 'bolder' },
  send_button: {
    width: '80%',
    backgroundColor: '#4caf50',
    color: 'black',
    padding: '14px 20px',
    margin: '8px 0',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '1.2rem'
  }
};

export default class WuphfForm extends React.Component {
  constructor() {
    super();

    this.state = {
      formFields: { from_name: '' }
    };
  }

  handleSubmit(formFields) {
    axios
      .post('/wuphf', formFields)
      .then(function(response) {
        console.log(response);
        //Perform action based on response
      })
      .catch(function(error) {
        console.log(error);
        //Perform action based on error
      });
  }

  inputChangeHandler(e) {
    let formFields = { ...this.state.formFields };
    formFields[e.target.name] = e.target.value;
    this.setState({
      formFields
    });
  }

  render() {
    return (
      <div style={styles.wuphf_form}>
        <form onsubmit={this.handleSubmit(this.state.formFields)}>
          <label for='from_name' style={styles.label}>
            Your Name
          </label>
          <input
            type='text'
            id='from_name'
            name='from_name'
            placeholder='Your name..'
            onChange={e => this.inputChangeHandler.call(this, e)}
            value={this.state.formFields.username}
            style={styles.input_type__text}
          />
          <button class='send-button'>Send Wuphf!</button>
        </form>
      </div>
    );
  }
}
