import React from 'react';
import axios from 'axios';
import './WuphfForm.css';

export default class WuphfForm extends React.Component {
  constructor(props) {
    super(props);

    var formFields = {};
    this.props.formOptions.forEach(option => {
      formFields[option.key] = '';
    });

    this.state = {
      formFields
    };
  }

  renderFormOptions() {
    console.log('rendering');
    console.log(this.props);
    this.props.formOptions.map(option => (
      <div>
        <label className='wuphf-form-label' htmlFor={option.key}>
          {option.labelText}
        </label>
        <inputkey
          type={option.inputType}
          id={option.key}
          name={option.key}
          placeholder={option.placeholder}
          onChange={this.handleChange.bind(this)}
          value={this.state.formFields[option.key]}
        />
      </div>
    ));
  }

  onFormSubmit(e) {
    e.preventDefault();
    console.log(this.state.formFields);
    axios
      .post('/api/wuphf', this.state.formFields)
      .then(function(response) {
        console.log(response);
        //Perform action based on response
      })
      .catch(function(error) {
        console.log(error);
        //Perform action based on error
      });
  }

  handleChange(e) {
    let formFields = { ...this.state.formFields };
    formFields[e.target.name] = e.target.value;
    console.log(formFields);
    this.setState({
      formFields
    });
  }

  render() {
    return (
      <div className='wuphf-form'>
        <form onSubmit={this.onFormSubmit.bind(this)}>
          {this.renderFormOptions(this)}
          <button className='send-button'>Send Wuphf!</button>
        </form>
      </div>
    );
  }
}
